var longestPalindrome = function (s) {
    let longStr = ''
    for (let i = 0; i < s.length; i++) {
        let index = s.indexOf(s[i], i + 1)
        if(longStr.length >= s.length - i) break
        while (index !== -1) {
            let str = s.slice(i, index + 1)
            if (str.length > longStr.length) {
                if (isPalindrome(str)) {
                    if (longStr.length < str.length) longStr = str
                }
            }
            index = s.indexOf(s[i], index + 1)
        }
    }
    function isPalindrome(str) {
        return str === str.split('').reverse().join('')
    }
    return longStr || s[0]
};

